DROP TABLE IF EXISTS `tbl_water_baptism_registration`; CREATE TABLE `tbl_water_baptism_registration` ( `registration_id` VARCHAR(45) NOT NULL, `member_id` VARCHAR(45) NOT NULL, `year` VARCHAR(4) NULL, `date_filed` DATE NULL, `profession` VARCHAR(100) NULL, `date_got_saved` DATE NULL, `baptism` VARCHAR(50) NULL, `baptism_location` VARCHAR(100) NULL, `desire_ministry` VARCHAR(100) NULL, `spouse_name` VARCHAR(100) NULL, `marriage_date` DATE NULL, `children` JSON NULL COMMENT 'JSON array of children: [{"name": "string", "age_gender": "string", "birthday": "MM/DD/YY"}]', `printed_name_signature` VARCHAR(100) NULL, `recorded_by` VARCHAR(100) NULL, `notes` TEXT NULL, `status` VARCHAR(45) NOT NULL DEFAULT 'pending', `date_created` DATETIME NOT NULL, `date_updated` DATETIME NULL, PRIMARY KEY (`registration_id`), INDEX `fk_water_baptism_registration_member_idx` (`member_id` ASC), CONSTRAINT `fk_water_baptism_registration_member` FOREIGN KEY (`member_id`) REFERENCES `tbl_members` (`member_id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci; 
